mapper.update:
update notice
set ncount = ncount + 1
where nNo = #{nNo}

update notice
set nTitle=#{nTitle}, nContent=#{nContent}, MODIFY_DATE=sysdate
where nNo = #{nNo}

update NOTICECOMMENTS 
set COMMENTS_STATUS = 'N'
WHERE COMMENTS_NO = #{comments_no}

update NOTICECOMMENTS
set COMMENTS_CONTENT = #{Comments_Content}
where COMMENTS_NO = #{Comments_No}
